export const theme = {
    colors: {
      black: '#000',
      white: '#fff',
      text: '#757575',
      background: '#dcdfe4',
      online: '#00ff00',
      offline: '#fd0900',
      backgroundLight:'#eeeff7',
    },
    space: [0, 2, 4, 8, 16, 32, 64, 128, 256, 516],
    fonts: {
      body: 'system-ui, sans-serif',
      heading: 'system-ui, sans-serif',
      monospace: 'Menlo, monospace',
    },
    fontSizes: {
      xs: '12px',
      s: '14px',
      m: '24px',
      l: '32px',
      xl: '64px',
    },
    fontWeights: {
      normal: 400,
      bold: 700,
    },
    lineHeights: {
      body: 1.5,
      heading: 1.125,
    },
    borders: {
      none: 'none',
      normal: '1px solid',
    },
    radii: {
      none: '0',
      normal: '4px',
      round: '50%',
    },
    table: {
    width: '100%',
    borderCollapse: 'separate',
    borderSpacing: 0,
    textAlign: center,
  }
  };
