export const WrapperProfile = styled.div`
padding: ${p => p.theme.space[2]}px;

  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  border-bottom: 1px solid var(--border-color);
`;
export const Description = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
